import { useEffect, useState } from "react";
import axios from "axios";

function ProductList() {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:5000/api/products")
            .then(res => setProducts(res.data))
            .catch(err => console.error(err));
    }, []);

    return (
        <div>
            <h2 style={{ textAlign: "center", marginBottom: "20px" }}>Productos</h2>
            <div style={{
                display: "grid",
                gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))", // Ajusta el ancho mínimo
                gap: "20px", // Más espacio entre los productos
                padding: "0 20px" // Añado algo de espacio interno
            }}>
                {products.map(product => (
                    <div
                        key={product._id}
                        style={{
                            border: "1px solid #ccc",
                            borderRadius: "8px",
                            padding: "10px",
                            textAlign: "center",
                            backgroundColor: "#fff",
                            boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)", // Agrego sombra suave
                            transition: "transform 0.2s", // Efecto de hover
                        }}
                    >
                        <img
                            src={product.image}
                            alt={product.name}
                            style={{
                                width: "100%",
                                height: "200px", // Fijo el alto para las imágenes
                                objectFit: "cover", // Asegura que la imagen se ajuste bien
                                borderRadius: "8px", // Bordes redondeados para las imágenes
                                marginBottom: "10px", // Espacio debajo de la imagen
                            }}
                        />
                        <h3 style={{ fontSize: "1.2em", margin: "10px 0" }}>{product.name}</h3>
                        <p><strong>Categoría:</strong> {product.category}</p>
                        <p><strong>Precio:</strong> ${product.price}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default ProductList;
